    updateImpressions: function() {
        var pageImpressions = this.mergeImpressions();
        var dlImpressions = {
            'event' : 'impression-load',
            'ecommerce' : {
                'currencyCode' : dlCurrencyCode,
                'impressions' : []
            }
        }
        var impressionCounter = 0;
        for (blockName in pageImpressions) {
            if (blockName === 'length' || !pageImpressions.hasOwnProperty(blockName)) continue;
            for (var i = 0; i < pageImpressions[blockName].length; i++) {
                var impression = pageImpressions[blockName][i];
                dlImpressions.ecommerce.impressions.push({
                    'id': impression.id,
                    'name': impression.name,
                    'brand': impression.brand,
                    'category': impression.category,
                    'list': impression.list,
                    'position': impression.position
                });
                categoryList = impression.list;
                categoryName = impression.category;
                impressionCounter++;
                this.bindImpressionClick(impression.id, impression.type, impression.name,
                    impression.category, impression.list, impression.position, blockName, impression.listPosition);
            }
        }
        if (impressionCounter > 0) {
            dataLayer.push(dlImpressions);
        }
    },

    mergeImpressions: function() {
        var pageImpressions = [];
        var blockNames = [
            'product_list',
            'product.info.upsell',
            'catalog.product.related',
            'checkout.cart.crosssell',
            'search_result_list'
        ];
        blockNames.each(function(blockName) {
            // check if there is a new block generated by FPC placeholder update
            if (blockName in updatedImpressions) {
                pageImpressions[blockName] = updatedImpressions[blockName];
            } else if (blockName in staticImpressions) { // use the static data otherwise
                pageImpressions[blockName] = staticImpressions[blockName];
            }
        });
        return pageImpressions;
    },

    updatePromotions : function() {
        var dlPromotions = {
            'event' : 'promotionView',
            'ecommerce': {
                'promoView': {
                    'promotions' : []
                }
            }
        };
        var pagePromotions = [];
        // check if there is a new block generated by FPC placeholder update
        if (updatedPromotions.length) {
            pagePromotions = updatedPromotions;
        }
        // use the static data otherwise
        if (pagePromotions.length == 0 && staticPromotions.length) {
            pagePromotions = staticPromotions;
        }
        var promotionCounter = 0;
        for (var i = 0; i < pagePromotions.length; i++) {
            var promotion = pagePromotions[i];
            var banner = $('salbanner-' + promotion.id);
            if (!banner || promotion.activated == '0') {
                continue;
            }
            dlPromotions.ecommerce.promoView.promotions.push({
                'id': promotion.id,
                'name': promotion.name,
                'creative': promotion.creative,
                'position': promotion.position
            });
            promotionCounter++;
            this.bindPromotionClick(banner, promotion.id, promotion.name, promotion.creative, promotion.position);

        }
        if (promotionCounter > 0) {
            dataLayer.push(dlPromotions);
        }
    },

