// old school cookie functions grabbed off the web

if (!window.Mage) var Mage = {};

Mage.Cookies = {};
Mage.Cookies.expires  = null;
Mage.Cookies.path     = '/';
Mage.Cookies.domain   = null;
Mage.Cookies.secure   = false;
Mage.Cookies.set = function(name, value){
     var argv = arguments;
     var argc = arguments.length;
     var expires = (argc > 2) ? argv[2] : Mage.Cookies.expires;
     var path = (argc > 3) ? argv[3] : Mage.Cookies.path;
     var domain = (argc > 4) ? argv[4] : Mage.Cookies.domain;
     var secure = (argc > 5) ? argv[5] : Mage.Cookies.secure;
     document.cookie = name + "=" + escape (value) +
       ((expires == null) ? "" : ("; expires=" + expires.toGMTString())) +
       ((path == null) ? "" : ("; path=" + path)) +
       ((domain == null) ? "" : ("; domain=" + domain)) +
       ((secure == true) ? "; secure" : "");
};

Mage.Cookies.get = function(name){
    var arg = name + "=";
    var alen = arg.length;
    var clen = document.cookie.length;
    var i = 0;
    var j = 0;
    while(i < clen){
        j = i + alen;
        if (document.cookie.substring(i, j) == arg)
            return Mage.Cookies.getCookieVal(j);
        i = document.cookie.indexOf(" ", i) + 1;
        if(i == 0)
            break;
    }
    return null;
};

Mage.Cookies.clear = function(name) {
  if(Mage.Cookies.get(name)){
    document.cookie = name + "=" +
    "; expires=Thu, 01-Jan-70 00:00:01 GMT";
  }
};

Mage.Cookies.getCookieVal = function(offset){
   var endstr = document.cookie.indexOf(";", offset);
   if(endstr == -1){
       endstr = document.cookie.length;
   }
   // function 'unescape' => decodeURIComponent (unescape is deprecated)
   return decodeURIComponent(document.cookie.substring(offset, endstr));
};

var AtalanValidator = Class.create();

AtalanValidator.prototype = {
    initialize : function(className, error, test, options) {
        if(typeof test == 'function'){
            this.options = $H(options);
            this._test = test;
        } else {
            this.options = $H(test);
            this._test = function(){
                return true
                };
        }
        this.error = error || 'AtalanValidation failed.';
        this.className = className;
    },
    test : function(v, elm) {
        return (this._test(v,elm) && this.options.all(function(p){
            return AtalanValidator.methods[p.key] ? AtalanValidator.methods[p.key](v,elm,p.value) : true;
        }));
    }
}
AtalanValidator.methods = {
    pattern : function(v,elm,opt) {
        return AtalanValidation.get('IsEmpty').test(v) || opt.test(v)
        },
    minLength : function(v,elm,opt) {
        return v.length >= opt
        },
    maxLength : function(v,elm,opt) {
        return v.length <= opt
        },
    min : function(v,elm,opt) {
        return v >= parseFloat(opt)
        },
    max : function(v,elm,opt) {
        return v <= parseFloat(opt)
        },
    notOneOf : function(v,elm,opt) {
        return $A(opt).all(function(value) {
            return v != value;
        })
        },
    oneOf : function(v,elm,opt) {
        return $A(opt).any(function(value) {
            return v == value;
        })
        },
    is : function(v,elm,opt) {
        return v == opt
        },
    isNot : function(v,elm,opt) {
        return v != opt
        },
    equalToField : function(v,elm,opt) {
        return v == $F(opt)
        },
    notEqualToField : function(v,elm,opt) {
        return v != $F(opt)
        },
    include : function(v,elm,opt) {
        return $A(opt).all(function(value) {
            return AtalanValidation.get(value).test(v,elm);
        })
        }
}

var AtalanValidation = Class.create();
AtalanValidation.defaultOptions = {
    onSubmit : true,
    stopOnFirst : false,
    immediate : false,
    focusOnError : true,
    useTitles : false,
    addClassNameToContainer: false,
    containerClassName: '.input-box',
    onFormValidate : function(result, form) {},
    onElementValidate : function(result, elm) {}
};

AtalanValidation.prototype = {
    initialize : function(form, options){
        this.form = $(form);
        if (!this.form) {
            return;
        }
        this.options = Object.extend({
            onSubmit : AtalanValidation.defaultOptions.onSubmit,
            stopOnFirst : AtalanValidation.defaultOptions.stopOnFirst,
            immediate : AtalanValidation.defaultOptions.immediate,
            focusOnError : AtalanValidation.defaultOptions.focusOnError,
            useTitles : AtalanValidation.defaultOptions.useTitles,
            onFormValidate : AtalanValidation.defaultOptions.onFormValidate,
            onElementValidate : AtalanValidation.defaultOptions.onElementValidate
        }, options || {});
        if(this.options.onSubmit) Event.observe(this.form,'submit',this.options.onSubmit.bind(this),false);
        if(this.options.immediate) {
            Form.getElements(this.form).each(function(input) { // Thanks Mike!                
                if (input.tagName.toLowerCase() == 'select') {
                    Event.observe(input, 'blur', this.onChange.bindAsEventListener(this));
                }
                Event.observe(input, 'change', this.onChange.bindAsEventListener(this));
            }, this);
        }
    },
    onChange : function (ev) {
        AtalanValidation.isOnChange = true;
        AtalanValidation.validate(Event.element(ev),{
            useTitle : this.options.useTitles, 
            onElementValidate : this.options.onElementValidate
        });
        AtalanValidation.isOnChange = false; 
    },
    onSubmit :  function(ev){
        if(!this.validate()) Event.stop(ev);
    },
    validate : function() {
        var result = false;
        var useTitles = this.options.useTitles;
        var callback = this.options.onElementValidate;
        try {
            if(this.options.stopOnFirst) {
                result = Form.getElements(this.form).all(function(elm) {
                    return AtalanValidation.validate(elm,{
                        useTitle : useTitles, 
                        onElementValidate : callback
                    });
                });
            } else {
                result = Form.getElements(this.form).collect(function(elm) {
                    return AtalanValidation.validate(elm,{
                        useTitle : useTitles, 
                        onElementValidate : callback
                    });
                }).all();
            }
        } catch (e) {

        }
        if(!result && this.options.focusOnError) {
            try{
                Form.getElements(this.form).findAll(function(elm){
                    return $(elm).hasClassName('validation-failed')
                    }).first().focus()
            }
            catch(e){

            }
        }
        this.options.onFormValidate(result, this.form);
        return result;
    },
    reset : function() {
        Form.getElements(this.form).each(AtalanValidation.reset);
    }
}

Object.extend(AtalanValidation, {
    validate : function(elm, options){
        options = Object.extend({
            useTitle : false,
            onElementValidate : function(result, elm) {}
        }, options || {});
        elm = $(elm);

        var cn = $w(elm.className);
        return result = cn.all(function(value) {
            var test = AtalanValidation.test(value,elm,options.useTitle);
            options.onElementValidate(test, elm);
            return test;
        });
    },
    insertAdvice : function(elm, advice){
        if(jQuery(elm).hasClass('pop-validation')) {
            createPopupFormMsg(advice, elm);
            return;
        }
		
        var container = $(elm).up('.field-row');
        if(container){
            Element.insert(container, {
                after: advice
            });
        } else if (elm.up('td.value')) {
            elm.up('td.value').insert({
                bottom: advice
            });
        } else if (elm.advaiceContainer && $(elm.advaiceContainer)) {
            $(elm.advaiceContainer).update(advice);
        }
        else {
            switch (elm.type.toLowerCase()) {
                case 'checkbox':
                case 'radio':
                    var p = elm.parentNode;
                    if(p) {
                        Element.insert(p, {
                            'bottom': advice
                        });
                    } else {
                        Element.insert(elm, {
                            'after': advice
                        });
                    }
                    break;
                default:
                    Element.insert(elm, {
                        'after': advice
                    });
            }
        }
    },
	
    showAdvice : function(elm, advice, adviceName){
        if(!elm.advices){
            elm.advices = new Hash();
        }
        else{
            elm.advices.each(function(pair){
                this.hideAdvice(elm, pair.value);
            }.bind(this));
        }
        elm.advices.set(adviceName, advice);
        if(jQuery(elm).hasClass('pop-validation')) {
            showPopupFormMsg(advice, elm);
            jQuery(advice).css({
                display: 'block', 
                opacity: 0
            }).fadeTo(500, 1);
            return;
        }
        if(typeof Effect == 'undefined') {
            advice.style.display = 'block';
        } else {
            if(!advice._adviceAbsolutize) {
                new Effect.Appear(advice, {
                    duration : 1
                });
            } else {
                Position.absolutize(advice);
                advice.show();
                advice.setStyle({
                    'top':advice._adviceTop,
                    'left': advice._adviceLeft,
                    'width': advice._adviceWidth,
                    'z-index': 1000
                });
                advice.addClassName('advice-absolute');
            }
        }
    },

    hideAdvice : function(elm, advice){
        if(advice != null) advice.hide();
    },
    updateCallback : function(elm, status) {
        if (typeof elm.callbackFunction != 'undefined') {
            eval(elm.callbackFunction+'(\''+elm.id+'\',\''+status+'\')');
        }
    },
    ajaxError : function(elm, errorMsg) {
        var name = 'validate-ajax';
        var advice = AtalanValidation.getAdvice(name, elm);
        if (advice == null) {
            advice = this.createAdvice(name, elm, false, errorMsg);
        }
        this.showAdvice(elm, advice, 'validate-ajax');
        this.updateCallback(elm, 'failed');

        elm.addClassName('validation-failed');
        elm.addClassName('validate-ajax');
        if (AtalanValidation.defaultOptions.addClassNameToContainer && AtalanValidation.defaultOptions.containerClassName != '') {
            var container = elm.up(AtalanValidation.defaultOptions.containerClassName);
            if (container && this.allowContainerClassName(elm)) {
                container.removeClassName('validation-passed');
                container.addClassName('validation-error');
            }
        }
    },
    allowContainerClassName: function (elm) {
        if (elm.type == 'radio' || elm.type == 'checkbox') {
            return elm.hasClassName('change-container-classname');
        }
        
        return true;
    },
    test : function(name, elm, useTitle) {
        var v = AtalanValidation.get(name);
        var prop = '__advice'+name.camelize();
        try {
            if(AtalanValidation.isVisible(elm) && !v.test($F(elm), elm)) {
                var advice = AtalanValidation.getAdvice(name, elm);
                if (advice == null) {
                    advice = this.createAdvice(name, elm, useTitle);
                }
                this.showAdvice(elm, advice, name);
                this.updateCallback(elm, 'failed');
                elm[prop] = 1;
                if (!elm.advaiceContainer) {
                    elm.removeClassName('validation-passed');
                    elm.addClassName('validation-failed');
                } 
            
                if (AtalanValidation.defaultOptions.addClassNameToContainer && AtalanValidation.defaultOptions.containerClassName != '') {
                    var container = elm.up(AtalanValidation.defaultOptions.containerClassName);
                    if (container && this.allowContainerClassName(elm)) {
                        container.removeClassName('validation-passed');
                        container.addClassName('validation-error');
                    }
                }
                return false;
            } else {
                var advice = AtalanValidation.getAdvice(name, elm);
                this.hideAdvice(elm, advice);
                this.updateCallback(elm, 'passed');
                elm[prop] = '';
                elm.removeClassName('validation-failed');
                elm.addClassName('validation-passed');
                if (AtalanValidation.defaultOptions.addClassNameToContainer && AtalanValidation.defaultOptions.containerClassName != '') {
                    var container = elm.up(AtalanValidation.defaultOptions.containerClassName);
                    if (container && !container.down('.validation-failed') && this.allowContainerClassName(elm)) {
                        if (!AtalanValidation.get('IsEmpty').test(elm.value) || !this.isVisible(elm)) { 
                            container.addClassName('validation-passed');
                        } else {
                            container.removeClassName('validation-passed');
                        }
                        container.removeClassName('validation-error');
                    }
                }
                return true;
            }
        } catch(e) {
            throw(e)
        }
    },
    isVisible : function(elm) {
        while(elm.tagName != 'BODY') {
            if(!$(elm).visible()) return false;
            elm = elm.parentNode;
        }
        return true;
    },
    getAdvice : function(name, elm) {
        return $('advice-' + name + '-' + AtalanValidation.getElmID(elm)) || $('advice-' + AtalanValidation.getElmID(elm));
    },
    createAdvice : function(name, elm, useTitle, customError) {
        var v = AtalanValidation.get(name);
        var errorMsg = useTitle ? ((elm && elm.title) ? elm.title : v.error) : v.error;
        if (customError) {
            errorMsg = customError;
        }
        try {
            if (Translator){
                errorMsg = Translator.translate(errorMsg);
            }
        }
        catch(e){}

        advice = '<div class="validation-advice" id="advice-' + name + '-' + AtalanValidation.getElmID(elm) +'" style="display:none">' + errorMsg + '</div>'

        AtalanValidation.insertAdvice(elm, advice);
        advice = AtalanValidation.getAdvice(name, elm);
        if($(elm).hasClassName('absolute-advice')) {
            var dimensions = $(elm).getDimensions();
            var originalPosition = Position.cumulativeOffset(elm);

            advice._adviceTop = (originalPosition[1] + dimensions.height) + 'px';
            advice._adviceLeft = (originalPosition[0])  + 'px';
            advice._adviceWidth = (dimensions.width)  + 'px';
            advice._adviceAbsolutize = true;
        }
        return advice;
    },
    getElmID : function(elm) {
        return elm.id ? elm.id : elm.name;
    },
    reset : function(elm) {
        elm = $(elm);
        var cn = $w(elm.className);
        cn.each(function(value) {
            var prop = '__advice'+value.camelize();
            if(elm[prop]) {
                var advice = AtalanValidation.getAdvice(value, elm);
                if (advice) {
                    advice.hide();
                }
                elm[prop] = '';
            }
            elm.removeClassName('validation-failed');
            elm.removeClassName('validation-passed');
            if (AtalanValidation.defaultOptions.addClassNameToContainer && AtalanValidation.defaultOptions.containerClassName != '') {
                var container = elm.up(AtalanValidation.defaultOptions.containerClassName);
                if (container) {
                    container.removeClassName('validation-passed');
                    container.removeClassName('validation-error');
                }
            }
        });
    },
    add : function(className, error, test, options) {
        var nv = {};
        nv[className] = new AtalanValidator(className, error, test, options);
        Object.extend(AtalanValidation.methods, nv);
    },
    addAllThese : function(validators) {
        var nv = {};
        $A(validators).each(function(value) {
            nv[value[0]] = new AtalanValidator(value[0], value[1], value[2], (value.length > 3 ? value[3] : {}));
        });
        Object.extend(AtalanValidation.methods, nv);
    },
    get : function(name) {
        return  AtalanValidation.methods[name] ? AtalanValidation.methods[name] : AtalanValidation.methods['_LikeNoIDIEverSaw_'];
    },
    methods : {
        '_LikeNoIDIEverSaw_' : new AtalanValidator('_LikeNoIDIEverSaw_','',{})
    }
});

