Atalan.DOB = Class.create();
Atalan.DOB.prototype = {
    initialize: function(selector, required, format) {
        var el        = $$(selector)[0];
        this.day      = Element.select($(el), '.dob-day')[0];
        this.month    = Element.select($(el), '.dob-month')[0];
        this.year     = Element.select($(el), '.dob-year')[0];
        this.dob      = Element.select($(el), '.dob-full')[0];
        this.advice   = Element.select($(el), '.validation-advice')[0];
        this.required = required;
        this.format   = format;

        this.year.validate = this.validate.bind(this);
        //this.day.validate = this.validate.bind(this);
        //this.year.validate = this.validate.bind(this);

        this.advice.hide();
    },

    validate: function() {
        var error = false;

        if (this.day.value=='' && this.month.value=='' && this.year.value=='') {
            if (this.required) {
                error = '    .';
            } else {
                this.dob.value = '';
            }
        } else if (this.day.value=='' || this.month.value=='' || this.year.value=='') {
            error = ',    .';
        } else {
            var date = new Date();
            if (this.day.value<1 || this.day.value>31) {
                error = ',    (1-31).';
            } else if (this.month.value<1 || this.month.value>12) {
                error = ',    (1-12).';
            } else if (this.year.value<1900 || this.year.value>date.getFullYear()) {
                error = ',    ('+(date.getFullYear()-110)+'-'+(date.getFullYear()-18)+').';
            } else {
                this.dob.value = this.format.replace(/(%m|%b)/i, this.month.value).replace(/(%d|%e)/i, this.day.value).replace(/%y/i, this.year.value);
                var testDOB = this.month.value + '/' + this.day.value + '/'+ this.year.value;
                var test = new Date(testDOB);
                if (isNaN(test)) {
                    error = ',    .';
                }
            }
        }
        if (error !== false) {
            try {
                this.advice.innerHTML = Translator.translate(error);
            }
            catch (e) {
                this.advice.innerHTML = error;
            }
            this.advice.show();
			this.day.addClassName('validation-failed');
			this.month.addClassName('validation-failed');
            return false;
        }

		this.day.removeClassName('validation-failed');
		this.month.removeClassName('validation-failed');
        this.advice.hide();
        return true;
    }
}

