function createPopup(code,msg,options)
{
    var popup_css = {
        position: "fixed",
        left: "50%",
        top: "50%"
    };
    /*if('position' in options)
        position = options['position'];*/
    pop = jQuery('#' + code);
    if(pop.length==0)
    {
        jQuery('body').append(
            '<div class="boxPopupIn"><div id="' + code + '" class="procuctListPopup" style="display: block;"><div class="top"></div><div class="cont"><div class="close"><a href="javascript: void(0)" title=""><span></span></a></div><p class="popcnt"></p></div><div class="bot"></div></div></div>'
        );
        pop = jQuery('#' + code);
    }
    pop.find('> .cont > .popcnt').html(msg).wrapInner('<strong />');
    if(pop.width())
        popup_css["margin-left"] = -(pop.width()/2);
    pop.css(popup_css);
    return pop;
}


