function setPLocation(url, setFocus){
    if (url.search('checkout/cart/add') != -1){ //CART ADD
        window.opener.focus();

        if (url[url.length-1] == '/') delim = '';
        else delim = '/';

        if (window.opener.location.pathname.search('checkout/cart') == -1)
            window.opener.ajaxcartsend(url+delim+'awacp/1', 'url', '');
        else
            window.opener.ajaxcartsend(url+delim+'awacp/1/is_checkout/1', 'url', '');
	}
	else{
		if(setFocus) {
			window.opener.focus();
		}
        window.opener.location.href = url;
	}

}

function ajaxcartsend(url, type, obj){
	url = getCommonUrl(url)

	//showProgressAnimation();
	showAjaxLoader();
	if (type == 'form'){		
		$_('#product_addtocart_form').action += url;
		$_('#product_addtocart_form').POST	(
			function(resp){
				if (typeof(resp) == 'string'){
					try{
						eval('resp = ' + resp);
					}catch(e){
						return obj.form.submit();
					}
				}
				//hideProgressAnimation();
				hideAjaxLoader();
				if (resp.r != 'success'){
                    if(resp.r == 'location') {
                        window.location.href = resp.url;
                    } else {
                        if (resp.r == 'error') {
                            var popup = createPopup('cartError',resp.message);
                            popup.show();
                        }else{
                            obj.form.submit();
                        }
                    }
				}
				else{
					if(AW_ACP.useConfirmation){
						showConfirmDialog();	
					}					
					updateCartView(resp);
				}
			}
		);
	}
	if (type == 'url'){
		$_(url).GET(
			function(resp){
				try{
					if (typeof(resp) == 'string') eval('resp = ' + resp);
				}catch(e){
					win.location.href=url;
					hideProgressAnimation();
					return;
				}
				hideAjaxLoader();
				//hideProgressAnimation();

				if (resp.r != 'success'){
					//win.location.href=url;
                    if(resp.r == 'error') {
                        var popup = createPopup('cartError',resp.message);
                        popup.show();
                    }
                    if(resp.r == 'location') {
                        window.location.href = resp.url;
                    }
				}
				else{
					if(AW_ACP.useConfirmation){
						showConfirmDialog();	
					}	
					updateCartView(resp);
				}
			}
		);
	}
}



function updateDeleteLinks(){
	var tmpLinks = document.links;//win.$_(aw_cartDivClass).getElementsByTagName('a',win.$_(aw_cartDivClass));
	for (i=0; i<tmpLinks.length; i++){
		if (tmpLinks[i].href.search('checkout/cart/delete') != -1){
			url = tmpLinks[i].href.replace(/\/uenc\/.+,/g, "");
			var del = url.match(/delete\/id\/\d+\//g);
			var id = del[0].match(/\d+/g);
			if (win.location.protocol == 'https:'){
				aw_base_url = aw_base_url.replace("http:", "https:");
			}	
			if(!AW_ACP.isCartPage){
				tmpLinks[i].href = '1javascript:ajaxcartprodelete("' + aw_base_url + 'ajaxcartpro/cart/remove/id/' + id +'")';
			}else{
				tmpLinks[i].href = 'javascript:ajaxcartprodelete("' + aw_base_url + 'ajaxcartpro/cart/remove/id/' + id +'/is_checkout/1")';
			}
			tmpLinks[i].onmouseup = 'GoogleAnalyticsUniversalCart.cartItemRemoved()';

		}
	}
}

