function adj_update_links(evt, className, isSingleVal)
{
    var link = Event.findElement(evt, '.filter__item'),
        sel = 'active';

    var checkbox = jQuery(link).find('input');
    
    if (link.hasClassName(sel)) {
        link.removeClassName(sel);
        checkbox.prop('checked', true);
    } else {
        link.addClassName(sel);
        checkbox.prop('checked', false);
    }

    //only one  price-range can be selected
    if (isSingleVal){
        var items = $('narrow-by-list').getElementsByClassName(className);
        var i, n = items.length;
        for (i=0; i<n; ++i) {
            var li = jQuery(items[i]).closest('li');
            if (li.hasClass(sel) && items[i].id != checkbox.prop('id'))
                li.removeClass(sel);
                jQuery(items[i]).prop('checked', false);
        }
    }

    adj_nav_add_params(checkbox.data('attribute'), checkbox.data('value').toString(), isSingleVal);

    adj_nav_make_request();
    
    Event.stop(evt);    
}


function adj_nav_attribute_listener(evt)
{
    adj_nav_add_params('p', 'clear', 1);
    adj_update_links(evt, 'adj-nav-attribute', 0);
}

