function adj_nav_clear_listener(evt)
{
    var link = Event.findElement(evt, 'A'),
        varName = link.id.split('-')[0];
    
    adj_nav_add_params('p', 'clear', 1);
    adj_nav_add_params(varName, 'clear', 1);
    
    if ('price' == varName){
        var from =  $('adj-nav-price-from'),
            to   = $('adj-nav-price-to');
          
        if (Object.isElement(from)){
            from.value = from.name;
            to.value   = to.name;
        }
    }
    
    adj_nav_make_request();    
    
    Event.stop(evt);  
}


function adj_nav_round(num){
    num = parseFloat(num);
    if (isNaN(num))
        num = 0;
        
    return Math.round(num);
}

function adj_nav_price_input_listener(evt){
    if (evt.type == 'keypress' && 13 != evt.keyCode)
        return;
    
    var numFrom = adj_nav_round($('adj-nav-price-from').value),
        numTo   = adj_nav_round($('adj-nav-price-to').value);
  
    if ((numFrom<0.01 && numTo<0.01) || numFrom<0 || numTo<0)   
        return;

    adj_nav_add_params('p', 'clear', 1);
    adj_nav_add_params('price', numFrom + ',' + numTo, true);
    adj_nav_make_request();         
}


function adj_nav_category_listener(evt){
    var currentParamsStr = jQuery('#adj-nav-params').val();
    currentParamsStr.split("&").forEach(function(part) {
        var item = part.split("=");
        if (['cat', 'limit', 'order', 'dir', 'isAjax'].indexOf(item[0]) == -1) {
            adj_nav_add_params(item[0], 'clear');
        }
    });
    adj_update_links(evt, 'adj-nav-category', 1);
}

function objectToUrlParams(obj) {
    var str = "";
    for (var key in obj) {
        if (str != "") {
            str += "&";
        }
        str += key + "=" + encodeURIComponent(obj[key]);
    }
    return str;
}

function adj_nav_toolbar_init()
{
    var toolbarChangedCallback = function(){
        adj_nav_toolbar_make_request(productListBlockView.toolbarView.getParams());
    };
    productListBlockView.toolbarView.currentSorting.subscribe(toolbarChangedCallback);
    productListBlockView.toolbarView.currentLimit.subscribe(toolbarChangedCallback);

    return;
    var items = jQuery('#adj-nav-container').find('.pages, .view-mode, .view-by, .sort-by');
    var i, n = items.length;
    for (i=0; i<n; ++i){
        jQuery(items[i]).on('select2:select', adj_nav_toolbar_listener);
        //Event.observe(items[i], 'select', adj_nav_toolbar_listener);
    }
}


function adj_nav_toolbar_make_request(params)
{
    adj_nav_add_params('p', 'clear', 1);
    var allToolbarParams = productListBlockView.toolbarView.getParamKeys();
    for(var paramKey in allToolbarParams) {
        adj_nav_add_params(paramKey, 'clear', 1);
        if(paramKey in params) {
            adj_nav_add_params(paramKey, params[paramKey], 1);
        }
    }
    adj_nav_make_request();
}

function adj_nav_toolbar_listener(evt){
    adj_nav_toolbar_make_request(evt.target.value);
    Event.stop(evt);
}


function adj_nav_dt_listener(evt){
    var e = Event.findElement(evt, 'DT');
    e.nextSiblings()[0].toggle();
    e.toggleClassName('adj-nav-dt-selected');
}

function adj_nav_clearall_listener(evt)
{
    var params = $('adj-nav-params').value.parseQuery();
    $('adj-nav-params').value = 'adjclear=true';
    if (params['q']) {
        $('adj-nav-params').value += '&q=' + params['q'];
    }
    if (params['ndspecialkey']) {
        $('adj-nav-params').value += '&ndspecialkey=1';
    }
    adj_nav_make_request();
    Event.stop(evt); 
}

