var deferredItemModel = function (itemJson, deferredManager) {
    var _warningMessageLifetime = 10000;
    var self = this;
    self.id = itemJson.id;
    self.product_id = itemJson.product_id;
    self.name = itemJson.name;
    self.qty = ko.observable(itemJson.qty);
    self.price = itemJson.price;
    self.oldPrice = itemJson.old_price;
    self.discountString = itemJson.discount_str;
    self.productUrl = itemJson.productUrl;
    self.thumbnail = itemJson.thumbnail;
    self.warningMessage = ko.observable('');
    self.notAvailable = ko.observable(itemJson.not_available);
    self.formatedPrice = formatPrice(self.price);
    self.warningMessage = ko.observable('');
    self.warningMessageFlag = ko.observable(false);
    self.additionalData = [];
    self.bundlePrice = formatPrice(itemJson.bundle_price);
    if ('additionalData' in itemJson) {
        for (var key in itemJson.additionalData) {
            self.additionalData.push(itemJson.additionalData[key]);
        }
    }

    self.total = ko.computed(function () {
        return self.price * self.qty();
    }, this);
    self.formatedTotal = ko.computed(function () {
        return formatPrice(self.total());
    }, this);

    self.oldTotal = ko.computed(function () {
        return self.oldPrice * self.qty();
    }, this);

    self.formatedOldTotal = ko.computed(function () {
        return formatPrice(self.oldTotal());
    }, this);

    self.waiting = ko.observable(false);
    self.removeCandidate = ko.observable(false);

    self.init = function () {
        self.qty.subscribe(function (newQty) {
            self.waiting(true);
            deferredManager._modifyItemQty(self.id, newQty);
        });

        self._warningMessageTimeOut = null;
        self.warningMessage.subscribe(function (newValue) {
            if (newValue) {
                self.warningMessageFlag(true);
            } else {
                self.warningMessageFlag(false);
            }
        });
        self.warningMessageFlag.subscribe(function (newValue) {
            if (newValue) {
                self._warningMessageTimeOut = window.setTimeout(function () {
                    self.warningMessageFlag(false);
                }, _warningMessageLifetime);
            } else {
                window.clearTimeout(self._warningMessageTimeOut);
            }
        });

        if ('messages' in itemJson) {
            var warningMessages = [];
            for (var messageType in itemJson.messages) {
                if (['notice', 'warning', 'error'].indexOf(messageType) != (-1)) {
                    for (var i = 0, n = itemJson.messages[messageType].length; i < n; i++) {
                        warningMessages.push(itemJson.messages[messageType][i]);
                    }
                }
            }
            if (warningMessages.length > 0) {
                self.warningMessage(warningMessages.join('<br>'));
            }
        }
    }

    self.init();

    /**
     *
     * @param item
     */
    self.removeItem = function (item) {
        self.isCartOverlayed = true;
        item.removeCandidate(false);
        deferredManager._removeFromDeferred(item.id, function () {
            self.isCartOverlayed = false;
        });
    };

    /**
     *
     * @param item
     */
    self.addToCart = function (item) {
        self.isCartOverlayed = true;
        item.removeCandidate(false);
        deferredManager._addToCart(item, function () {
            self.isCartOverlayed = false;
        });
    };
}


