//collecting select options
function populateSelectOptions(me) {
	me._options.innerHTML = "";
    var have_selected_option = false;
	for(var w = 0; w < me.options.length; w++) {
		
		var optionHolder = document.createElement('li');
		var optionLink = document.createElement('a');
		var optionTxt;
		if (me.options[w].title.indexOf('image') != -1) {
			optionTxt = document.createElement('img');
			optionSpan = document.createElement('span');
			optionTxt.src = me.options[w].title;
			optionSpan = document.createTextNode(me.options[w].text);
		} else {
			optionTxt = document.createTextNode(me.options[w].text);
		}

		if(me.options[w].value.search(/http/i)!=-1)
		{
			//optionLink.href = me.options[w].value;
			me.options[w].value = me.options[w].value.replace(/(&|\?)p=1/gi, "");
			optionLink.href = "javascript: selectMe('"+me.id+"',"+w+","+me._number+"); hideSelectOptionsId("+me._number+",'"+me.options[w].value+"');";
		}
		else
		{
			optionLink.href ="javascript:showOptions("+me._number+"); selectMe('"+me.id+"',"+w+","+me._number+");";
		}
		//
		if (me.options[w].title.indexOf('image') != -1) {
			optionLink.appendChild(optionTxt);
			optionLink.appendChild(optionSpan);
		} else {
			optionLink.appendChild(optionTxt);
		}
		optionHolder.appendChild(optionLink);
		me._options.appendChild(optionHolder);
		//check for pre-selected items
		if(me.options[w].selected) {
			selectMe(me.id,w,me._number);
            have_selected_option = true;
		}
	}
    if(!have_selected_option){
        w = 0;
        var selected_options = jQuery(me).find('option:selected');
        have_selected = false;
        jQuery(me).find('option').each(function(){
            if(jQuery(this).is(":selected")){
                have_selected = true;
                return false;
            }
            w++;
        });

        if(!have_selected)
            w = 0;
        selectMe(me.id,w,me._number);
    }

	if (me.disabled) {
		me._disabled.style.display = "block";
	}
	else {
		me._disabled.style.display = "none";
	}
}

