jQuery(function() {

	var $legalForm = jQuery('.au-legal-form'),
		$overlay = jQuery('.au-legal-popup-overlay'),
		$popup = jQuery('#form-done');

	jQuery('input.masked').each(function() {

		var $this = jQuery(this);

		if($this.hasClass('masked-phone')) {
			$this.mask("+7(999)999-99-99");
		}

		if($this.hasClass('masked-inn')) {
			$this.mask("9999999999");
		}
	});

	var validateForm = {
		errorClass: "au-error",
		errorNode: jQuery('.au-legal-form-errors'),
		errorText: {
			hasEmpty: ", ,  ."
		},
		isValid: true,
		isEmptyField: function($input) {
			return ($input.val() === '');
		},
		doValidate: function($inputs) {
			var context = this;
			this.isValid = true;
			this.errorNode.text('');
			$inputs.removeClass(context.errorClass);
			$inputs.each(function(item) {
				var $this = jQuery(this);
				if(context.isEmptyField($this)) {
					$this.addClass(context.errorClass);
					context.errorNode.text(context.errorText.hasEmpty);
					context.isValid = false;
				}
			});
		}
	};

	function showPopup($id) {
		$overlay.fadeIn();
		$id.css({
			left: (jQuery(window).width() - $id.width())/2,
			top: (jQuery(window).height() - $id.height())/2,
		}).fadeIn();

		$overlay.on('click', function() {
			$overlay.fadeOut();
			$id.fadeOut();
		})
	}

	$legalForm.on('submit', function(e) {

		e.preventDefault();

		var $this = jQuery(this),
			$input = $this.find('.au-input-field');

		var data = $legalForm.serialize();

		var url = $legalForm.attr("action");

		validateForm.doValidate($input);
		if(validateForm.isValid) {
			jQuery.ajax(url, {
				data: data,
				dataType: 'json',
				method: 'post',
				success: function (data) {
					if (data['success'] == 1) {
						showPopup(jQuery('#form-done'));
					} else {
						console.log(data['message'])
					}
				}
			});

		}

		return false;

	});

});

