        function changeBtnText(param) {
            var btn = $j('.topnav__search-more-link.more-link.js-more-link');
            if (param == 'hide') {
                $j(btn).addClass('open');
                $j(btn).text($j(btn).data('hide-label'));
            } else if (param == 'show') {
                $j(btn).removeClass('open');
                $j(btn).text($j(btn).data('show-label'));
            } else if (param == 'expand') {
                $j(btn).removeClass('open');
                $j(btn).text($j(btn).data('expand-label'));
            }
        }

