function isValidEmailAddress(emailAddress) {
    var pattern = new RegExp(/^(("[\w-+\s]+")|([\w-+]+(?:\.[\w-+]+)*)|("[\w-+\s]+")([\w-+]+(?:\.[\w-+]+)*))(@((?:[\w-+]+\.)*\w[\w-+]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][\d]\.|1[\d]{2}\.|[\d]{1,2}\.))((25[0-5]|2[0-4][\d]|1[\d]{2}|[\d]{1,2})\.){2}(25[0-5]|2[0-4][\d]|1[\d]{2}|[\d]{1,2})\]?$)/i);
    return pattern.test(emailAddress);
};

Array.prototype.remove = function() {
    var what, a = arguments, L = a.length, ax;
    while (L && this.length) {
        what = a[--L];
        while ((ax = this.indexOf(what)) !== -1) {
            this.splice(ax, 1);
        }
    }
    return this;
};

function join(arr /*, separator */) {
    var separator = arguments.length > 1 ? arguments[1] : ", ";
    return arr.filter(function (n) {
        return n
    }).join(separator);
}

function showModalOverlayUnderObj(obj) {
	var $par = obj.parents(".modalOverlay");

	if ($par && !$par.hasClass("modalOverlay--open")) {
		$par.addClass("modalOverlay--open");
		obj.addClass("modalOverlayOver");


		$par.bind('click.lkmodalOverlay', function(e) {
			if (checkEventTarget(e,"modalOverlayOver")) {
				hideModalOverlayUnderObj(obj);
			}
		});
	}
}

 function hideModalOverlayUnderObj(obj) {
	var $par = obj.parents(".modalOverlay");
	if ($par && $par.hasClass("modalOverlay--open")) {
		$par.removeClass("modalOverlay--open");
		obj.removeClass("modalOverlayOver");
		$par.unbind("click.lkmodalOverlay");
	}
}


function checkEventTarget(ev,className) {
    var $target = jQuery(ev.target);
    //return (!$target.parents("."+className).length && !$target.hasClass(className) && !$target.is(trigger));
    return (!$target.parents("."+className).length && !$target.hasClass(className));
	//return (!$target.closest("."+className).length);
}

function activize(elem) {
	elem.addClass("active").siblings().removeClass("active");
}

function focusFormField(lbl,fld,parent) {
	var val = fld.val();
	if(fld && fld.is("input") && val.trim() !== '' ) {
		parent.addClass('field--filled');
	}

	var onInputFocus = function(ev) {
		parent.addClass('field--filled');
	}

	var	onInputBlur = function(ev) {
		if( ev.target.value.trim() === '') {
			parent.removeClass('field--filled');
		}
	}

	fld.on('focus', onInputFocus);
	fld.on('blur', onInputBlur);
}

