function addBannerToGridMode() {
    var gridArticles = jQuery('.products__list.products__list--grid .products__item');
    if(gridArticles.length > 0 && imageUrl) {
        var bodyWidth = document.body.clientWidth;
        // after 2d row
        var rowArticle;
        if(bodyWidth >= 1424) {
            rowArticle = gridArticles[7];
        } else if(bodyWidth >= 1024) {
            rowArticle = gridArticles[5];
        } else if(bodyWidth >= 768) {
            rowArticle = gridArticles[3];
        }
        if(rowArticle) {
            jQuery(rowArticle).after(bannerBlock);
        }
    }
}

function addBannerToListMode() {
    var listArticles = jQuery('.products__list.products__list--list .products__item');
    if(listArticles.length > 0 && imageUrl) {
        // after 3d element
        var rowArticle = listArticles[2];
        if(rowArticle) {
            jQuery(rowArticle).after(bannerBlock);
        }
    }
}

function showMyCardsPage() {
    var saveButton = jQuery(".apay__btn--pay");
    var cancelButton = jQuery(".apay__btn--cancel");
    var addCardForm = jQuery(".apay__addnew-card");
    var addCardMobileForm = jQuery(".apay__cards .apay__cards__mobile-list__add-new");
    var backLink = jQuery(".js-paccount__back-link");
    var cardsList = jQuery(".apay__cards");
    var editItem = jQuery('.paccount__block >.paccount__block-content');
    var cardsListContainer = jQuery('.paccount__block-content-container');
    var addNewCardButton = jQuery('.js-apay__cards-item-div-addnew');
    var title = jQuery('#main_block_header');
    saveButton.attr('disabled', 'disabled');
    cancelButton.attr('disabled', 'disabled');
    addCardMobileForm.removeClass('hidden');
    cardsList.removeClass('hidden');
    backLink.attr('data-level', 1);
    cardsListContainer.removeClass('hidden');
    addNewCardButton.removeClass('active');
    addCardForm.addClass("hidden");
    editItem.css('display', 'none');
    if (site.isMobile) {
        title.text(' ');
    }
}

function showCardPage(cardItem) {
    var tokenId  = cardItem.attr('tid');
    var editItem = jQuery('.paccount__block-content[tid="' + tokenId + '"]');
    var cardsListContainer = jQuery('.paccount__block-content-container');
    var backLink = jQuery(".js-paccount__back-link");
    var deleteButton = jQuery('.apay__btn--cancel');
    editItem.css('display', 'block');
    cardsListContainer.addClass('hidden');
    deleteButton.removeAttr('disabled');
    backLink.attr('data-level', 'cards');
}

function showAddCardForm() {
    var par = jQuery('.apay__cards.modalOverlay');
    var list = par.find('.apay__cards-list');
    var cardsList = jQuery('.apay__cards');
    var addButton = jQuery('.apay__addnew-card');
    var title = jQuery('#main_block_header');
    var saveButton = jQuery('.apay__btn--pay');
    var cancelButton = jQuery('.apay__btn--cancel');
    var backLink = jQuery(".js-paccount__back-link");
    var addNewCardButton = jQuery('.js-apay__cards-item-div-addnew');
    addButton.removeClass('hidden');
    addNewCardButton.addClass('active');
    if (site.isMobile) {
        cardsList.addClass('hidden');
        hideModalOverlayUnderObj(list);
        title.text(' ');
    }
    saveButton.removeAttr('disabled');
    cancelButton.removeAttr('disabled');
    backLink.attr('data-level', 'cards');
}

function switchMyCardBlock() {
    if (!jQuery('.apay__addnew-card').is(':visible')) {
        showAddCardForm();
    } else {
        showMyCardsPage();
    }
};

